async function reInvestPoolIfAny(poolName) {
    const pid = pools[poolName].pid
    const address = pools[poolName].address
    var threshold = pools[poolName].threshold * multiplier
    let aggressiveThreshold = threshold * 0.9

    // Check if cached reward is available
    var reward = pools[poolName].reward
    var checkedAt = pools[poolName].checkedAt

    // Skip check if
    // - reward > 0 AND reward < `aggressiveThreshold`
    // - checkedAt 
    if (checkedAt && reward
        && reward > 0 && reward < aggressiveThreshold
        && moment().diff(moment(checkedAt), 'seconds') < 60) {
        console.log(`[Skipped] ${poolName} - Reward: ${reward} - LastCheckedAt: ${checkedAt}`)
        return
    }

    var holdingCake = await pancakeswap.getHoldingCake(address)
    var pendingCake = await pancakeswap.getPendingCakeFromChef(pid, address)
    var totalPending = parseInt(holdingCake, 10) + parseInt(pendingCake, 10)
    
    var totalPendingInDecimal = totalPending / Math.pow(10, 18)
    
    pools[poolName].checkedAt = moment()
    pools[poolName].reward = totalPendingInDecimal

    if (totalPendingInDecimal >= threshold) {
        // Reinvest with default gas price (5 gwei)
        var success = await reinvest(poolName)
        if (success) {
            return totalPendingInDecimal
        }

        return 0
    } else {
        // Only print pool that near thresold
        if (totalPendingInDecimal >= aggressiveThreshold) {
            console.log(`[Be Ready] ${poolName}: ${totalPendingInDecimal} CAKE`)
        }
        
        return 0
    }
}

// ====
async function start() {
    while(true){
        try {
            // Update global gasPrice
            // gasPrice = parseInt(await web3.eth.getGasPrice())
            let gasPrice = await javis.getGasPrice()
            if (gasPrice == 0) {
                await utils.sleep(10000)
                continue
            }

            // Adjust reward multiplier
            if (gasPrice < parseInt(web3.utils.toWei('6', 'gwei'), 10)) {
                multiplier = 1
            } else if (gasPrice < parseInt(web3.utils.toWei('7', 'gwei'), 10)) {
                multiplier = 1.2
            } else if (gasPrice < parseInt(web3.utils.toWei('8', 'gwei'), 10)) {
                multiplier = 1.4
            } else if (gasPrice < parseInt(web3.utils.toWei('9', 'gwei'), 10)) {
                multiplier = 1.6
            } else if (gasPrice < parseInt(web3.utils.toWei('10', 'gwei'), 10)) {
                multiplier = 1.8
            } else if (gasPrice < parseInt(web3.utils.toWei('11', 'gwei'), 10)) {
                multiplier = 2
            } else if (gasPrice < parseInt(web3.utils.toWei('12', 'gwei'), 10)) {
                multiplier = 2.2
            } else if (gasPrice < parseInt(web3.utils.toWei('13', 'gwei'), 10)) {
                multiplier = 2.4
            } else if (gasPrice < parseInt(web3.utils.toWei('14', 'gwei'), 10)) {
                multiplier = 2.5
            } else if (gasPrice < parseInt(web3.utils.toWei('15', 'gwei'), 10)) {
                multiplier = 2.6
            } else {
                multiplier = 3
            }

            console.log(`Gas Price ${gasPrice}; Threshold ${baseline * multiplier} (x${multiplier})`)
            for (var poolName in pools) {
                await reInvestPoolIfAny(poolName)
                await utils.sleep(100)
            }

            
        }
        catch (err) {
            // console.error(err);
        }

        console.log("=======")
            await utils.sleep(10000)
     }
}

start()


